import { useState, useRef, useMemo } from 'react';
import { data } from "../../utils/data";
import { Tab,CurrencyIcon,Button,ConstructorElement } from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './burgerConstructor.module.css';

export default function burgerConstruction(props) {
    

    return(
        <section>
            <div>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
      <ConstructorElement
        type="top"
        isLocked={true}
        text="Краторная булка N-200i (верх)"
        price={200}
        // thumbnail={img}
      />
      <ConstructorElement
        text="Краторная булка N-200i (верх)"
        price={50}
        // thumbnail={img}
      />
      <ConstructorElement
        type="bottom"
        isLocked={true}
        text="Краторная булка N-200i (низ)"
        price={200}
        // thumbnail={img}
      />
    </div>
            </div> 

            <div>
                <p></p>
                <CurrencyIcon type="primary" />

                <Button htmlType="button" type="primary" size="large">
                    Оформить заказ 
                </Button>
            </div>
        </section>
    )
}
