import { useState, useRef, useMemo } from 'react';
import { data } from "../../utils/data";
import { CurrencyIcon,Button,ConstructorElement,DragIcon } from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './burgerConstructor.module.css';
import ModalOverlay from '../modalOverlay/modalOverlay';

export default function BurgerConstruction(props) {
    
    const handleModal = () => {
        console.log('1');
        return ReactDOM.createPortal((
            <section>
                <h1>Hey</h1>
            </section>
        ),modalRoot
        );;
    };

    return(
        <section className='ml-4 mt-25 ml-10'>
            <div className={styles.drag}>
                <div className='mb-10 mr-2' style={{ display: 'flex', justifyContent: 'space-around' ,flexDirection: 'column', gap: '10px' }}>
                    {/* <DragIcon type="none" /> */}
                    <DragIcon type="primary" />
                    {/* <DragIcon type="primary" /> */}
                </div>
            

                <div className='mb-10' style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                    <ConstructorElement
                        type="top"
                        isLocked={true}
                        text="Краторная булка N-200i (верх)"
                        price={200}
                        thumbnail={props.img}
                    />
                    <div className={styles.scroll}>
                        <ConstructorElement
                            text="Краторная булка N-200i (верх)"
                            price={50}
                            thumbnail={props.img}
                        />
                    </div>
                    <ConstructorElement
                        type="bottom"
                        isLocked={true}
                        text="Краторная булка N-200i (низ)"
                        price={200}
                        thumbnail={props.img}
                    />
                </div>
            </div>

            <div className={styles.footer}>
                <div className='mr-10 text text_type_digits-medium' style={{display: 'flex',alignItems:"center"}}>
                    <p className='mb-1 mt-1 mr-1'>610</p>
                    <CurrencyIcon type="primary" />
                </div>
                <Button htmlType="button" type="primary" size="large" onClick={handleModal}>
                    Оформить заказ 
                </Button>
            </div>
        </section>
    )
}
