import React from "react";
import ReactDOM from "react-dom";
import { CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './modal.module.css';
import closeIcon from '../images/closeIcon.svg'

const modalRoot = document.getElementById("react-modals");

class modal extends React.Component {

    buttonClick = () => {
        let popup = document.querySelector(".popup_opened");
        popup.className.toggle(styles.popup_close);
    }

    render() {
        return ReactDOM.createPortal((
            <section className={styles.popup_opened} >
                <div>
                    <h1 className='ml-10 mt-10'></h1>
                    <button className='mt-15 mr-10' onClick={this.buttonClick}>
                        <img src={closeIcon} alt="closeIcon" />
                    </button>
                </div>
            </section>),modalRoot
        );
    }
}
export default modal;
