            <section className={styles.modalOverlay} id ="modalOverlay" state = {this.state.visible} setState = {this.handleOpenModal}>
                <Modal header = {header} onClose={this.handleCloseModal}>
                    {children}
                </Modal>
            </section>
        ),modalRoot
        );
    }
}
