import {useState} from "react";

const fetchPromise = fetch('https://norma.nomoreparties.space/api/ingredients');

export default function App() {
    const [arr, setArr] = useState(' ');

    const changeArr = () => {
        fetchPromise
        .then(res => {
            if (!res.ok) {
                throw new Error('Network response was not ok');
            }
            return res.json();
        })
        .then(data => console.log('Результат: ',data))
        .then(data => {
            setArr({arr: data});
            console.logthis.state.arr();
            return this.state.arr;
        })
        .catch(err =>console.log('Ошибка вызова:', err))

        
    }
    return changeArr;
}
