import { useState, useRef, useMemo } from 'react';
import { data } from "../../utils/data";
import { Tab,CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './burgerIngredients.module.css';


export default class BurgerIngredients extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
    visible: false,
    current: "one",
    };
    
    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);

  }

  handleOpenModal() {
      this.setState({ visible: true });
  }
  handleCloseModal() {
      this.setState({ visible: false });
  }

  componentDidMount() {
      document.addEventListener("keydown", (e) => {
          if (e.code === 'Escape') {
              this.setState({ visible: false });
          }
      });
  }

  componentDidUpdate() {
      const modalOverlay = document.getElementById("modalOverlay");
      if (modalOverlay) {
          modalOverlay.addEventListener('click', (evt) => {
              evt.preventDefault();
              if (evt.target.id === 'modalOverlay') {
                  this.setState({ visible: false });
              }
          })
      }
  }
    
    
  render() {

    return(
      <section style={{maxWidth:"600px"}}>
        <h1 className='text text_type_main-large mt-10 mb-5'>Соберите бургер</h1>
        <div className='mb-10' style={{ display: 'flex' }}>
          <Tab value="one" active={current === 'one'} onClick={setCurrent}>
            Булки
          </Tab>
          <Tab value="two" active={current === 'two'} onClick={setCurrent}>
            Соусы
          </Tab>
          <Tab value="three" active={current === 'three'} onClick={setCurrent}>
            Начинки
          </Tab>
        </div>
  
        <div className={styles.scroll}>
          <h2 className='text text_type_main-medium mb-6'>Булки</h2>
        
          
          <div className="ml-4 mb-10" style={{display: "flex", flexWrap:"wrap"}}>
              {props.data.map(it => {
                if(it.type==="bun") {
                return(
                  <div className={styles.ingredient} key={it._id}>
                      <img src={it.image} alt={it.name} className="ml-4 mr-4 mt-6"/>
                      <div style={{display: "flex"}} className="mt-1 mb-1">
                          <p className="text text_type_digits-default mr-1">{it.price}</p>
                          <CurrencyIcon type="primary" />
                      </div>
                      <h3 className="text text_type_main-small mr-6" style={{textAlign: "center"}}>{it.name}</h3>
                  </div> 
                )}
              })}
          </div>
      
    
          <h2 className='text text_type_main-medium mt-10 mb-6'>Соусы</h2>
    
          
          <div className="ml-4 mb-10" style={{display: "flex", flexWrap:"wrap"}}>
          {props.data.map(it => {
                if(it.type==="sauce") {
                return(
                  <div className={styles.ingredient} key={it._id}>
                      <img src={it.image} alt={it.name} className="ml-4 mr-4 mt-6"/>
                      <div style={{display: "flex"}} className="mt-1 mb-1">
                          <p className="text text_type_digits-default mr-1">{it.price}</p>
                          <CurrencyIcon type="primary" />
                      </div>
                      <h3 className="text text_type_main-small mr-6" style={{textAlign: "center"}}>{it.name}</h3>
                  </div> 
                )}
              })}
          </div>
          
    
          <h2 className='text text_type_main-medium mt-10 mb-6'>Начинки</h2>
    
          
          <div className="ml-4 mb-10" style={{display: "flex", flexWrap:"wrap"}}>
          {props.data.map(it => {
                if(it.type==="main") {
                return(
                  <div className={styles.ingredient} key={it._id}>
                      <img src={it.image} alt={it.name} className="ml-4 mr-4 mt-6"/>
                      <div style={{display: "flex"}} className="mt-1 mb-1">
                          <p className="text text_type_digits-default mr-1">{it.price}</p>
                          <CurrencyIcon type="primary" />
                      </div>
                      <h3 className="text text_type_main-small mr-6"  style={{textAlign: "center"}}>{it.name}</h3>
                  </div> 
                )}
              })}
          </div>
        </div>

      </section>
    )
  }
}
