import React from "react";
import ReactDOM from "react-dom";
import { CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './modal.module.css';
import closeIcon from '../images/closeIcon.svg'

export default class Modal extends React.Component {

    render() {
        const { children, header, onClose } = this.props;
        
        return (
                <div className={styles.modalWindow}>
                    <h1 className='ml-10 mt-10 text text_type_main-large'>{header}</h1>
                    <button className='mt-15 mr-10' style={{border: 'none', padding: '0'}}>
                        <img className={styles.closeButton} src={closeIcon} alt="closeIcon" onClick={onClose}/>
                        <svg fill="none" viewBox="0 0 18 18">
<path fill="#F2F2F3" fill-rule="evenodd" d="M.2929.2929c.3905-.3905 1.0237-.3905 1.4142 0L9 7.5858l7.2929-7.293c.3905-.3904 1.0237-.3904 1.4142 0 .3905.3906.3905 1.0238 0 1.4143L10.4142 9l7.2929 7.2929c.3905.3905.3905 1.0237 0 1.4142-.3905.3905-1.0237.3905-1.4142 0L9 10.4142l-7.2929 7.2929c-.3905.3905-1.0237.3905-1.4142 0-.3905-.3905-.3905-1.0237 0-1.4142L7.5858 9 .2928 1.7071C-.0975 1.3166-.0975.6834.2929.293Z" clip-rule="evenodd"/>
</svg>
                    </button>
                    {children}
                </div>)
    }
}
