import {useState} from "react";

const fetchPromise = fetch('https://norma.nomoreparties.space/api/ingredients');

export default function App() {

    return (
fetchPromise
        .then(res => {
            if (!res.ok) {
                throw new Error('Network response was not ok');
            }
            return res.json();
        })
        .then(data => console.log(data))
        .then(data => {
            
            return data;
        })
        .catch(err =>console.log('Ошибка вызова:', err))
    );
}
