import React from "react";
import ReactDOM from "react-dom";
import { CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './modal.module.css';
import closeIcon from '../images/closeIcon.svg'

export default class Modal extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
        visible: false,
        };
        
        this.handleCloseModal = this.handleCloseModal.bind(this);
    }

    handleCloseModal() {
        this.setState({ visible: false });
    }

    componentDidMount() {
        document.addEventListener("keydown", (e) => {
            if (this.state.visible)
            if (e.code == 'Escape') {
                this.setState({ visible: false });
            }
        });

        document.addEventListener("click", (e) => {

            this.setState({ visible: false });
        });
    }


    render() {
        const { children, header } = this.props;
        
        return (
            <div className={styles.modalWindow}>
                <h1 className='ml-10 mt-10 text text_type_main-large'>{header}</h1>
                <button className='mt-15 mr-10' style={{border: 'none', padding: '0',backgroundColor: "rgba(0,0,0,0)"}}>
                    <img className={styles.closeButton} src={closeIcon} alt="closeIcon" onClick={this.handleCloseModal}/>
                </button>
                {children}
            </div>
        )
    }
}
