import {useState} from "react";

const fetchPromise = fetch('https://norma.nomoreparties.space/api/ingredients');

export default function app() {
    const [arr, setArr] = useState(0);

    const changeArr = () => {
        fetchPromise
        .then(res => {
            if (!res.ok) {
                throw new Error('Network response was not ok');
            }
            return res.json();
        })
        .then(data => console.log('Результат: ',data))
        .then(data => {
            return data;
        })
        .catch(err =>console.log('Ошибка вызова:', err))
    }
    return;
}
